mapper.insert:
insert into QUESTION_ANSWER
     ( QA_NO
     , QA_CONTENT
     , QA_WRITER
     , QA_SECRET
     , USER_ID
     , SPC_NO
     )
     values
     ( SEQ_QA.nextval
     , #{qaContent}
     , #{qaWriter}
     , #{qaSecret}
     , #{userId}
     , #{spaceNo}
     )

  		insert into REPORT
		       ( REPORT_NO

		       , SPC_NO


		       , REVIEW_NO

		       , REPORT_TYPE1
		       , REPORT_TYPE2
		       , REPORT_WRITER
		       , REPORT_TARGET
		       , REPORT_CONTENT
		       )
		       VALUES
		       ( SEQ_REPORT.NEXTVAL

		       , #{spcNo}


		       , #{reviewNo}

		       , #{reportType1}
		       , #{reportType2}
		       , #{reportWriter}
		       , #{reportTarget}
		       , #{reportContent}
		       )

  		insert into WISH
		       ( 
		       	 USER_ID
		       , SPC_NO
		       )
		       VALUES
		       ( 
		         #{userId}
		       , #{spcNo} 
		       )

mapper.delete:
  		DELETE FROM WISH
		WHERE USER_ID = #{userId}
		  AND SPC_NO = #{spcNo}

mapper.select:
  	SELECT COUNT(*)
	  FROM WISH
	WHERE USER_ID = #{userId}
	  AND SPC_NO = #{spcNo}
