		     , business_loc = #{businessLoc}
		     , business_acc = #{businessAcc}
             , business_phone = #{businessPhone}
             , business_email = #{businessEmail}
		 where user_id = #{userId}

  	update
	  	   question_answer
	   set qa_answer = #{qaAnswer}
	      ,qa_answerdate = sysdate
	 where qa_no = #{qaNo}

